<script>
    import Banner from '@/components/banner'
    import sectionTitle from '@/components/section-title'
    import comment from '@/components/comment'
    import menuTree from '@/components/menu-tree'
    import {fetchArticle, fetchComment, fetchToArticleCancelUpvote, fetchToArticleUpvote} from '../api'
    import {int} from "mockjs/src/mock/random/basic";
    export default {
        name: 'articles',
        data(){
          return{
              showDonate: false,
              comments: [],
              menus: [],
              postList: [],
              article_upvote: false
          }
        },
        components: {
            Banner,
            sectionTitle,
            comment,
            menuTree
        },
        methods: {
          fetchArticle() {
            console.log(this.$route.params.id)
            fetchArticle(this.$route.params.id).then(res => {
              this.postList = res.data.data || []
            }).catch(err => {
              console.log(err)
            })
          },
          getComment(){
              fetchComment().then(res => {
                  this.comments = res.data || []
              }).catch(err => {
                  console.log(err)
              })
          },
          fetchH(arr,left,right){
              if (right) {
                  return arr.filter(item => item.offsetTop > left && item.offsetTop < right)
              }else {
                  return arr.filter(item => item.offsetTop > left)
              }
          },
          // 生成目录
          createMenus(){
              let arr = []
              for(let i=6;i>0;i--){
                  let temp = []
                  let e = document.querySelector(".entry-content").querySelectorAll(`h${i}`)
                console.log("-------------",e)
                for (let j=0;j<e.length;j++){
                      let child = this.fetchH(arr,e[j].offsetTop,(j+1 === e.length)?undefined:e[j+1].offsetTop)
                  console.log("---------------------------", e[j].id)
                  temp.push({
                          h: i,
                          title: e[j].innerText,
                          id: e[j].id,
                          offsetTop: e[j].offsetTop,
                          child
                      })
                  }
                  if (temp.length){
                      arr = temp
                  }
              }
              this.menus = arr
          },
          upvote() {
              if (!JSON.parse(localStorage.getItem("userinfo"))['is_login']) {
                  this.$message({
                      message: '请先登录',
                      type: 'warning'
                  })
                  return
              }
              let data = {
                article_id : parseInt(this.$route.params.id)
              }
              fetchToArticleUpvote(data, localStorage.getItem("token")).then(res => {
                if (res.data.data.like_num) {
                  this.postList.like_num = res.data.data.like_num
                }
                let info = {
                  [this.$route.params.id] : true,
                }
                localStorage.setItem("article_upvote", JSON.stringify(info))
                this.$nextTick(() => {
                  this.article_upvote = true
                });
                this.$message({
                  message: '点赞成功',
                  type: 'success'
                })
              }).catch(err => {
                console.log(err)
              })
            },
          downvote() {
            if (!JSON.parse(localStorage.getItem("userinfo"))['is_login']) {
              this.$message({
                message: '请先登录',
                type: 'warning'
              })
              return
            }
            let data = {
              article_id : parseInt(this.$route.params.id)
            }
            fetchToArticleCancelUpvote(data, localStorage.getItem("token")).then(res => {
              if (res.data.data.like_num) {
                this.postList.like_num = res.data.data.like_num
              }
              let info = {
                [this.$route.params.id] : false,
              }
              localStorage.setItem("article_upvote", JSON.stringify(info))
              this.$nextTick(() => {
                this.article_upvote = false
              });
              this.$message({
                message: '取消点赞成功',
                type: 'success'
              })
            }).catch(err => {
              console.log(err)
            })
          }
        },
        mounted(){
            this.fetchArticle()
            this.article_upvote = JSON.parse(localStorage.getItem("article_upvote"))[this.$route.params.id]
        },
        created() {
            this.getComment()
        },
        watch: {
          postList(){
              this.$nextTick(() => {
                  this.createMenus()
              })
          },
        }
    }
</script>
